function playQuestionAudio() {
  const question = questions[currentQuestionIndex];

  // Strip LaTeX and clean text
  let plainText = question.text
    .replace(/\\\[.*?\\\]/gs, '')
    .replace(/\\\(.*?\\\)/gs, '')
    .replace(/\\text\{([^}]+)\}/g, '$1')
    .replace(/\s+/g, ' ')
    .trim();

  // Add dramatic pauses and emphasis
  plainText = `Now listen carefully... Question ${currentQuestionIndex + 1}. ${plainText}. Pay attention to the question...`;

  const utterance = new SpeechSynthesisUtterance(plainText);
  utterance.lang = 'en-GB'; // British English sounds closer to Nigerian lecture style
  utterance.pitch = 0.85;
  utterance.rate = 0.85;
  utterance.volume = 1;

  // Pick a deeper male voice if available
  const voices = speechSynthesis.getVoices();
  const nigerianVibeVoice = voices.find(v =>
    v.name.toLowerCase().includes('english') &&
    v.name.toLowerCase().includes('male')
  );
  if (nigerianVibeVoice) {
    utterance.voice = nigerianVibeVoice;
  }

  speechSynthesis.cancel();
  speechSynthesis.speak(utterance);
}

window.speechSynthesis.onvoiceschanged = () => {
  speechSynthesis.getVoices(); // populates voice list
};

function renderMarkdownMathSafe(rawText) {
  const markdownHtml = marked.parse(rawText || "");

  // Wrap unescaped math manually using RegEx (basic pattern)
  const mathWrapped = markdownHtml.replace(/(\d+\.?\d*\s*\\times\s*10\^\{-?\d+\})/g, (match) => {
    return `\\(${match}\\)`;
  });

  return mathWrapped;
}
